mapper.insert:

DECLARE
    query varchar2(500);
    tablename varchar2(100);
    uid number(38,0);
BEGIN
uid := ${id};
tablename := 'blog_user_'||uid||'';
query := 'CREATE TABLE '||tablename||' (
    id number(38,0),
    user_id number(38,0),
    title varchar2(100) not null,
    content varchar2(280) not null,
    regdate date default sysdate,
    updatedate date default sysdate,
    PRIMARY KEY(id),
    FOREIGN KEY(user_id) REFERENCES usertbl(id)
)';
dbms_output.put_line(query);
EXECUTE IMMEDIATE 'SELECT * FROM '||tablename||'';
EXCEPTION
WHEN OTHERS THEN
IF SQLCODE = -942 THEN
EXECUTE IMMEDIATE query;
END IF;
END;

mapper.update:
UPDATE blog_user_${userId} 
	SET
user_id=#{userId,jdbcType=NUMERIC}, title=#{title,jdbcType=VARCHAR}, content=#{content,jdbcType=VARCHAR},
updatedate=SYSDATE WHERE id=#{id}
